import { BlockedUser } from './blocked-user'
import { getRepository } from "typeorm";
import { Online } from './online'
import { auth } from 'express-openid-connect'
import axios from 'axios'
import { sendMessage } from '../web-socket'

export type User = Record<'name' | 'picture' | 'email' | 'sub', string>

export class Controller {
    async blockUser(data: BlockedUser): Promise<void> {
        await getRepository(BlockedUser).save(data)
        sendMessage({
            data: {
                message: 'user-blocked',
                data: data.senderId
            },
            sub: data.recipientId
        })
    }
    async unblockUser(data: BlockedUser): Promise<void> {
        await getRepository(BlockedUser).delete(data)
        sendMessage({
            data: {
                message: 'user-unblocked',
                data: data.senderId
            },
            sub: data.recipientId
        })
    }
    async getBlockedUsers(sub: string): Promise<string[]> {
        return (await getRepository(BlockedUser)
            .createQueryBuilder('b')
            .where('b.recipientId != :sub', { sub }).getMany())
            .map(({ senderId }) => senderId)
    }
    async getOnlineUsers(sub: string): Promise<User[]> {
        const usersOnline: User[] = []
        for (const id of (await getRepository(Online)
            .createQueryBuilder('o')
            .where('o.userId != :sub', { sub }).getMany())
            .map(({ userId }) => userId)) {
            const data = (await axios.get(`https://dev-judahdonkor-incubator.us.auth0.com/api/v2/users/${id}`, {
                headers: { authorization: 'Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IlkwTmtYd3ZRYXNldU9VcnA2ODNwWSJ9..BLMfXgU6-qTDgazTJBL_cXzFYuqaAIAA6cl-wduHvaZk-MF9xywS4mnTyJJTterTX-ZiVuNbihcN6MPL6biGSW0Lq7hN6r93l5SAiwvJVBhXotl2I-ELxro1urcDCNyQ6YrB8JG4rKhAup7kujyxq009S2mEcRlc0JqQlsEjnbaFQ5sI3_e_yvOV2FCuP2F29EQG_QZgwB5NSbdsvv81apTEOEjGSQBPkqmNIzk8mOz6srnIbGz0PvJdAyvY7q8QrChMm47Dh249wp2DiK5DC3m02dthgZZ2khHb3efKWwJBIvSkJj4DZheDqpgnpx5qJHgO-Zb_t5dYjZW3zAtQLQ' }
            })).data
            usersOnline.push({
                email: data.email,
                picture: data.picture,
                name: data.name,
                sub: data.user_id
            } as User)
        }

        return usersOnline
    }
    async addOnlineUser(userId: string): Promise<void> {
        await getRepository(Online).save({
            userId
        })
        sendMessage({
            data: {
                message: 'user-online',
                data: userId
            }
        })
    }
    async removeOnlineUser(userId: string): Promise<void> {
        await getRepository(Online).delete(userId)
        sendMessage({
            data: {
                message: 'user-offline',
                data: userId
            }
        })
    }
}

export { router } from './router'

export {
    Online, BlockedUser
}